
<main role="main">
  <div id="panner" class="carousel slide" data-ride="carousel">
      <ol class="carousel-indicators">
        <li data-target="#panner" data-slide-to="0" class="active"></li>
        <li data-target="#panner" data-slide-to="1"></li>
        <li data-target="#panner" data-slide-to="2"></li>
      </ol>
        <div class="carousel-inner">
          <div class="carousel-item active">
            <img class="d-block w-100" src="img/slide/slide1.jpg" alt="First slide">
          </div>
          <div class="carousel-item">
            <img class="d-block w-100" src="img/slide/slide2.jpg" alt="Second slide">
          </div>
          <div class="carousel-item">
            <img class="d-block w-100" src="img/slide/slide3.jpg" alt="Third slide">
          </div>
        </div>
        <a class="carousel-control-prev" href="#panner" role="button" data-slide="prev">
          <span class="carousel-control-prev-icon" aria-hidden="true"></span>
          <span class="sr-only">Previous</span>
        </a>
        <a class="carousel-control-next" href="#panner" role="button" data-slide="next">
          <span class="carousel-control-next-icon" aria-hidden="true"></span>
          <span class="sr-only">Next</span>
        </a>
      </div>
  <div class="row mt-3 py-1">
    <div class="container">
  <!-- /./ -->
    <label for="">Lọc dữ liệu chỉ định:</label>
    <div class="irow row">
      <div class="col-sm-3">
      <div class="input-group mb-3">
          <div class="input-group-prepend">
              <span class="input-group-text">Từ khóa</span>
            </div>
          <input type="text" id="tu-khoa" class="custom-select" placeholder="Nhập từ khóa cần tìm" value="">
          </div>
      </div>

      <div class="col-sm-3">
        <div class="input-group mb-3">
          <div class="input-group-prepend">
              <span class="input-group-text">Mã số</span>
            </div>
          <input type="text" id="ma-code" class="custom-select" placeholder="Nhập mã số code" value="">
          </div>
      </div>

      <div class="col-sm-3 mb-1">
        <select id="type" class="custom-select">
            <option value="0">-- Tất cả các bài viết gần nhất</option>
            <option value="1">-- Lọc ra các bài viết Miễn phí</option>
            <option value="2">-- Lọc ra các bài viết Có Phí</option>
          </select>
      </div>
      <div class="col-sm-3">
      <div class="form-group">
        <button type="button" class="btn-sm btn-danger" onclick="loc()" id="btn-loc">LỌC</button>
          <button style="font-weight:bold" class="btn-sm btn-info" onclick="btn_macdinh();" id="macdinh"> Mặc định</button>
          <button style="font-weight:bold" class="btn-sm btn-primary" onclick="btn_reload();" id="reload"> Tất Cả</button>
        </div>
      </div>
    <!-- Source load -->
    <div class="col-12 text-center">
      <div class="row mt-2 text-left" id="load-post"></div>
      <div style="display:none" id="error"><h4>Không tìm thấy bài viết</h4></div>
      <div class="mb-5" style="display:none" id="loading">
      <img class="mt-5 img-responsive center-block" style="width:110px;height:auto" src="/img/load_feeds.svg">
      </div>  
      </div>
      
    </div>
    <!-- /./ -->
   
    </div>
  </div>
</main>
